mapper.select:
SELECT u.* FROM eb_user AS u

    INNER JOIN eb_user_token AS ut on u.uid = ut.uid and ut.type = 1


    INNER JOIN eb_user_token AS ut on u.uid = ut.uid and ut.type = 2

where 1 = 1

    and u.is_promoter = ${isPromoter}


    and u.group_id in (${groupId})


    and ${tagIdSql}


    and u.level in (${level})


    and u.sex = ${sex}


    and u.country = #{country}


    and u.addres like '%${addres}'



        and u.pay_count = 0


        and u.pay_count >= ${payCount}



    and u.`status` = ${status}


    and u.`is_general_agency` = ${isGeneralAgency}





            and u.create_time between #{startTime} and #{endTime}
            and u.create_time = last_login_time


            and u.last_login_time between #{startTime} and #{endTime}


            and u.last_login_time not between #{startTime} and #{endTime}


            and u.create_time between #{startTime} and #{endTime}




    and ( u.phone like #{keywords, jdbcType=VARCHAR}
    or u.nickname like "%"#{keywords, jdbcType=VARCHAR}"%"
    or u.mark like #{keywords, jdbcType=VARCHAR})

ORDER BY u.uid desc
